INSERT INTO `wp_posts` VALUES (9,2,'2020-07-22 11:27:25','2020-07-22 16:27:25','<!-- wp:atomic-blocks/ab-columns {\"columns\":1,\"layout\":\"one-column\"} -->\n<div class=\"wp-block-atomic-blocks-ab-columns ab-layout-columns-1 one-column\"><div class=\"ab-layout-column-wrap ab-block-layout-column-gap-2 ab-is-responsive-column\"><!-- wp:atomic-blocks/ab-column {\"backgroundColor\":\"light-green-cyan\",\"className\":\"home__testimonials_shadow\"} -->\n<div class=\"wp-block-atomic-blocks-ab-column home__testimonials_shadow ab-block-layout-column\"><div class=\"ab-block-layout-column-inner has-light-green-cyan-background-color\"><!-- wp:heading {\"align\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\">Testimonials</h3>\n<!-- /wp:heading -->\n\n<!-- wp:atomic-blocks/ab-testimonial {\"testimonialImgID\":203,\"className\":\"home__testimonial\"} -->\n<div style=\"background-color:#f2f2f2;color:#32373c\" class=\"wp-block-atomic-blocks-ab-testimonial home__testimonial left-aligned ab-has-avatar ab-font-size-18 ab-block-testimonial\"><div class=\"ab-testimonial-text\"><p>Having these benefits gave me and my family peace of mind.</p></div><div class=\"ab-testimonial-info\"><div class=\"ab-testimonial-avatar-wrap\"><div class=\"ab-testimonial-image-wrap\"><img class=\"ab-testimonial-avatar\" src=\"http://drivers-benefits.local/wp-content/uploads/2020/08/image-testimonial-1-avatar.png\" alt=\"avatar\"/></div></div><h2 class=\"ab-testimonial-name\" style=\"color:#32373c\">Gupta Aditi</h2><small class=\"ab-testimonial-title\" style=\"color:#32373c\">Uber</small></div></div>\n<!-- /wp:atomic-blocks/ab-testimonial -->\n\n<!-- wp:atomic-blocks/ab-testimonial {\"testimonialImgID\":217,\"className\":\"home__testimonial\"} -->\n<div style=\"background-color:#f2f2f2;color:#32373c\" class=\"wp-block-atomic-blocks-ab-testimonial home__testimonial left-aligned ab-has-avatar ab-font-size-18 ab-block-testimonial\"><div class=\"ab-testimonial-text\"><p>I\'m so grateful for the support I\'ve received from Drivers Benefits!</p></div><div class=\"ab-testimonial-info\"><div class=\"ab-testimonial-avatar-wrap\"><div class=\"ab-testimonial-image-wrap\"><img class=\"ab-testimonial-avatar\" src=\"http://drivers-benefits.local/wp-content/uploads/2020/08/image-testimonial-2-avatar.jpg\" alt=\"avatar\"/></div></div><h2 class=\"ab-testimonial-name\" style=\"color:#32373c\">Weiling Chu</h2><small class=\"ab-testimonial-title\" style=\"color:#32373c\">Lyft</small></div></div>\n<!-- /wp:atomic-blocks/ab-testimonial -->\n\n<!-- wp:atomic-blocks/ab-testimonial {\"testimonialImgID\":217,\"className\":\"home__testimonial\"} -->\n<div style=\"background-color:#f2f2f2;color:#32373c\" class=\"wp-block-atomic-blocks-ab-testimonial home__testimonial left-aligned ab-has-avatar ab-font-size-18 ab-block-testimonial\"><div class=\"ab-testimonial-text\"><p>Alex is awesome!</p></div><div class=\"ab-testimonial-info\"><div class=\"ab-testimonial-avatar-wrap\"><div class=\"ab-testimonial-image-wrap\"><img class=\"ab-testimonial-avatar\" src=\"http://drivers-benefits.local/wp-content/uploads/2020/08/image-testimonial-2-avatar.jpg\" alt=\"avatar\"/></div></div><h2 class=\"ab-testimonial-name\" style=\"color:#32373c\">Weiling Chu</h2><small class=\"ab-testimonial-title\" style=\"color:#32373c\">Lyft</small></div></div>\n<!-- /wp:atomic-blocks/ab-testimonial -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:atomic-blocks/ab-column --></div></div>\n<!-- /wp:atomic-blocks/ab-columns -->\n\n<!-- wp:atomic-blocks/ab-columns {\"columns\":1,\"layout\":\"one-column\"} -->\n<div class=\"wp-block-atomic-blocks-ab-columns ab-layout-columns-1 one-column\"><div class=\"ab-layout-column-wrap ab-block-layout-column-gap-2 ab-is-responsive-column\"><!-- wp:atomic-blocks/ab-column {\"backgroundColor\":\"pale-cyan-blue\",\"className\":\"home__benefits_shadow\"} -->\n<div class=\"wp-block-atomic-blocks-ab-column home__benefits_shadow ab-block-layout-column\"><div class=\"ab-block-layout-column-inner has-pale-cyan-blue-background-color\"><!-- wp:heading {\"align\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\">Benefits</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Do not edit the first line of the benefit name (in bold). It is used to create the url slug of the benefits page.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:atomic-blocks/ab-profile-box {\"profileImgID\":168} -->\n<div style=\"background-color:#f2f2f2;color:#32373c\" class=\"wp-block-atomic-blocks-ab-profile-box square ab-has-avatar ab-font-size-18 ab-block-profile ab-profile-columns\"><div class=\"ab-profile-column ab-profile-avatar-wrap\"><div class=\"ab-profile-image-wrap\"><figure class=\"ab-profile-image-square\"><img class=\"ab-profile-avatar wp-image-168\" src=\"http://drivers-benefits.local/wp-content/uploads/2020/08/benefit-telemedicine.png\" alt=\"\"/></figure></div></div><div class=\"ab-profile-column ab-profile-content-wrap\"><h2 class=\"ab-profile-name\" style=\"color:#32373c\">telemedicine</h2><p class=\"ab-profile-title\" style=\"color:#32373c\">Telemedicine</p><div class=\"ab-profile-text\"><p>Speak to a doctor anytime, anywhere</p></div><ul class=\"ab-social-links\"></ul></div></div>\n<!-- /wp:atomic-blocks/ab-profile-box -->\n\n<!-- wp:atomic-blocks/ab-profile-box {\"profileImgID\":190} -->\n<div style=\"background-color:#f2f2f2;color:#32373c\" class=\"wp-block-atomic-blocks-ab-profile-box square ab-has-avatar ab-font-size-18 ab-block-profile ab-profile-columns\"><div class=\"ab-profile-column ab-profile-avatar-wrap\"><div class=\"ab-profile-image-wrap\"><figure class=\"ab-profile-image-square\"><img class=\"ab-profile-avatar wp-image-190\" src=\"http://drivers-benefits.local/wp-content/uploads/2020/08/benefit-classes.png\" alt=\"\"/></figure></div></div><div class=\"ab-profile-column ab-profile-content-wrap\"><h2 class=\"ab-profile-name\" style=\"color:#32373c\">classes</h2><p class=\"ab-profile-title\" style=\"color: