INSERT INTO `wp_posts` VALUES (232,2,'2020-08-23 20:29:25','2020-08-24 01:29:25','<!-- wp:group {\"customBackgroundColor\":\"#3c5ea8\",\"textColor\":\"very-light-gray\",\"align\":\"full\",\"className\":\"pt-4 pb-4 mb-5\"} -->\n<div class=\"wp-block-group alignfull has-very-light-gray-color has-text-color has-background pt-4 pb-4 mb-5\" style=\"background-color:#3c5ea8\"><div class=\"wp-block-group__inner-container\"><!-- wp:group {\"className\":\"container-narrow\"} -->\n<div class=\"wp-block-group container-narrow\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"level\":3} -->\n<h3>COVID-19 Updates for Drivers</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>During this difficult time Drivers Benefits is compiling information and providing research specifically for New York drivers for rideshare app companies and traditional Black Car services such as Vital. We are frequently updating the below resources.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"container-narrow\"} -->\n<div class=\"wp-block-group container-narrow\"><div class=\"wp-block-group__inner-container\"><!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"customBackgroundColor\":\"#bd1e1e\",\"borderRadius\":8,\"className\":\"covid__link_button\"} -->\n<div class=\"wp-block-button covid__link_button\"><a class=\"wp-block-button__link has-background\" href=\"/covid-19/health-safety\" style=\"background-color:#bd1e1e;border-radius:8px\" rel=\"\">Health &amp; Safety</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph {\"className\":\"mb-5\"} -->\n<p class=\"mb-5\"><a href=\"/covid-19/health-safety\">Read</a>&nbsp;what COVID-19 is, how to stay safe, and what you should do if you feel sick. Plus, watch videos on how drivers can keep themselves and their cars clean, and how to make a free appointment with a doctor from your phone.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"customBackgroundColor\":\"#bd1e1e\",\"borderRadius\":8,\"className\":\"covid__link_button\"} -->\n<div class=\"wp-block-button covid__link_button\"><a class=\"wp-block-button__link has-background\" href=\"/covid-19/financial-resources\" style=\"background-color:#bd1e1e;border-radius:8px\" rel=\"\">Financial Resources</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph {\"className\":\"mb-5\"} -->\n<p class=\"mb-5\"><a href=\"/covid-19/financial-resources\">Discover</a>&nbsp;help available to you on a variety of subjects, including job opportunities, financial assistance from app companies, driving expenses, food, health insurance, and more!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"customBackgroundColor\":\"#bd1e1e\",\"borderRadius\":8,\"className\":\"covid__link_button\"} -->\n<div class=\"wp-block-button covid__link_button\"><a class=\"wp-block-button__link has-background\" href=\"/covid-19/unemployment-guide\" style=\"background-color:#bd1e1e;border-radius:8px\" rel=\"\">Unemployment Guide</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph {\"className\":\"mb-5\"} -->\n<p class=\"mb-5\"><a href=\"/covid-19/unemployment-guide\">Learn</a>&nbsp;about New York State Unemployment Insurance, Pandemic Unemployment Assistance, and Pandemic Unemployment Compensation and how to apply.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"customBackgroundColor\":\"#bd1e1e\",\"borderRadius\":8,\"className\":\"covid__link_button\"} -->\n<div class=\"wp-block-button covid__link_button\"><a class=\"wp-block-button__link has-background\" href=\"/covid-19/paycheck-protection-loan\" style=\"background-color:#bd1e1e;border-radius:8px\" rel=\"\">Paycheck Protection Loan</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph {\"className\":\"mb-5\"} -->\n<p class=\"mb-5\"><a href=\"/covid-19/paycheck-protection-loan\">Lorem</a> ipsum</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"customBackgroundColor\":\"#bd1e1e\",\"borderRadius\":8,\"className\":\"covid__link_button\"} -->\n<div class=\"wp-block-button covid__link_button\"><a class=\"wp-block-button__link has-background\" href=\"/covid-19/immigration\" style=\"background-color:#bd1e1e;border-radius:8px\" rel=\"\">Immigration</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph {\"className\":\"mb-5\"} -->\n<p class=\"mb-5\"><a href=\"/covid-19/immigration\">Get</a>&nbsp;answers to your questions about being an immigrant in NY during the pandemic, including information on public charge and enrolling in government benefits like food stamps and Medicaid.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->','COVID-19','','publish','closed','closed','','covid-19','','','2020-09-02 18:12:21','2020-09-02 23:12:21','',0,'http://drivers-benefits.local/?page_id=232',0,'page','',0);
