INSERT INTO `wp_posts` VALUES (536,2,'2020-09-03 23:37:39','2020-09-04 04:37:39','<!-- wp:group {\"className\":\"container-full\"} -->\n<div class=\"wp-block-group container-full\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"level\":3} -->\n<h3>Contact Us</h3>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":496,\"sizeSlug\":\"large\",\"className\":\"image__responsive_header\"} -->\n<figure class=\"wp-block-image size-large image__responsive_header\"><img src=\"http://drivers-benefits.local/wp-content/uploads/2020/09/image-contact.png\" alt=\"\" class=\"wp-image-496\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:list {\"className\":\"pl-3\"} -->\n<ul class=\"pl-3\"><li><strong>Are you ready to become a Drivers Benefits member?</strong></li><li><strong>Do you have questions about using your benefits?</strong></li><li><strong>Do you have any feedback about Drivers Benefits?</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p>Our multilingual team of benefits experts is ready to help you with these and any other questions you may have.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Call us at&nbsp;<a href=\"tel:18338148590\">1-833-814-8590</a>&nbsp;to speak to a benefits expert or to make an in-person appointment. Our customer service center is open from Monday to Friday from 8am – 8pm.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"darkblue\"} -->\n<h3 class=\"has-darkblue-color has-text-color\">Address</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><strong>954 Lexington Avenue, #2043</strong><br><strong>New York, NY 10021</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://goo.gl/maps/kbTX6ie6Yb1sBGWb7\" target=\"_blank\" rel=\"noreferrer noopener\">View on map</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"darkblue\"} -->\n<h3 class=\"has-darkblue-color has-text-color\">Drop us a line</h3>\n<!-- /wp:heading -->\n\n<!-- wp:wpforms/form-selector {\"formId\":\"493\",\"className\":\"contact__form\"} /--></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group -->','Contact','','inherit','closed','closed','','491-revision-v1','','','2020-09-03 23:37:39','2020-09-04 04:37:39','',491,'http://drivers-benefits.local/news/491-revision-v1/',0,'revision','',0);
